// Import statements
import React, { useEffect, useState } from 'react';
import '../Style/Home.css'; 

const Home = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        async function product_data() {
            const response = await fetch('https://dummyjson.com/products');
            const data = await response.json();
            setProducts(data.products);
        }
        product_data();
    }, []);

    return (
        <div className="product-container">
            <h2>Home</h2>
            {products.map((data) => (
                <div key={data.id} className="proCard">
                    <div>
                        <img className="product_img" src={data.thumbnail} alt="" />
                    </div>

                    <div className="card_content">
                        <div className="brand">{data.title}</div>
                        <div className="description">{data.description}</div>
                        <div className="price">
                            <span className='priceProduct'>{data.price * 70}</span> &nbsp;
                            <span className='mrp'>M.R.P</span> &nbsp;
                            <strike>{data.price * 80}</strike>
                            <span>(10% off)</span>
                        </div>
                    </div>

                    <button>View</button>
                </div>
            ))}
        </div>
    );
};

export default Home;
